import ADD_PERSON from '../constant'
const initState = { id: "001", name: "zhangsan", age: 17 }
function personReducer(preState = initState, action) {
  const { type, data } = action;
  switch (type) {
    case ADD_PERSON:
      return [data, ...preState]
    default:
      break;
  }
}
