import { INCREMENT, DECREMENT } from '../constant';

//reducers是用来初始化状态和加工
const initState = 0;
export default function countReducers(preState = initState, action) {
  const { type, data } = actions;
  switch (type) {
    case INCREMENT:
      return preState + data;
    case DECREMENT:
      return preState - data
    default:
      return preState
  }
}
