import React, { Component } from 'react'

export default class Person extends Component {
  addPerson = () => {
    const { name } = this.inputNameValue;
    const { age } = this.inputAgeValue;


    console.log(name, age);
  }
  render() {
    return (
      <div>
        <input type="text" placeholder="请输入姓名" ref={c => this.inputNameValue = c} />&nbsp;
        <input type="text" placeholder="请输入年龄" ref={c => this.inputAgeValue = c} />&nbsp;
        <button onClick={this.addPerson}>添加</button>&nbsp;
        <ul>
          <li></li>
        </ul>
      </div>
    )
  }
}
